mapper.update:
UPDATE acl_role_permission
SET is_deleted = 1
WHERE role_id = #{roleId}
    AND permission_id IN

        #{permissionId}

    AND is_deleted = 0

mapper.select:
SELECT id, role_id, permission_id ,create_time, update_time, is_deleted
FROM acl_role_permission
WHERE role_id = #{roleId}
  AND permission_id = #{permissionId}

mapper.update:
